function UserForm() {
    return (
        <React.Fragment>
            <Container className="text-center my-4">
                <h2>{title}</h2>
            </Container>
            {!loading ? (
                <Form
                    onSubmit={submitForm}
                    className="col-md-6 bg-dark py-4 px-5"
                    style={{ display: "block", margin: "0 auto" }}
                >
                    <Form.Group>
                        <Row>
                            <Col>
                                <Form.Control
                                    required={true}
                                    name="username"
                                    value={user.username}
                                    onChange={newValues}
                                    className="my-4"
                                    type={"text"}
                                    placeholder="User name"
                                />
                                <Form.Select
                                    required
                                    name="contact"
                                    onChange={newValues}
                                    className="form-select"
                                    id="select"
                                    defaultValue={""}
                                >
                                    <option value="" disabled>
                                        Contacts
                                    </option>
                                    {contacts.map((contact, index) => (
                                        <option value={contact.mobile} key={index}>
                                            {contact.mobile}
                                        </option>
                                    ))}
                                </Form.Select>
                            </Col>
                            <Col>
                                <Form.Control
                                    required={true}
                                    name="photo"
                                    value={user.photo}
                                    onChange={newValues}
                                    className="my-4"
                                    type={"text"}
                                    placeholder="Contact photo Url"
                                />
                                <Image src={user.photo} className="user-photo" alt="contact" />
                            </Col>
                            <Row>
                                <Form.Control
                                    className="col btn btn-outline-light my-4 mx-1"
                                    type={"submit"}
                                    value={btnValue}
                                />
                                <LinkContainer to={"/users/page"} className="col my-4">
                                    <Button variant="light">Cancel</Button>
                                </LinkContainer>
                            </Row>
                        </Row>
                    </Form.Group>
                </Form>
            ) : (
                ""
            )}
        </React.Fragment>
    )
}

export default UserForm;
