    //             <input
    //               required={true}
    //               name="name"
    //               value={contact.name}
    //               onChange={newValues}
    //               className="form-control my-4"
    //               type={"text"}
    //               placeholder="Contact name"
    //             />
    //             <input
    //               required={true}
    //               name="mobile"
    //               value={contact.mobile}
    //               onChange={newValues}
    //               className="form-control my-4"
    //               type={"tel"}
    //               placeholder="Contact mobile"
    //             />
    //             <input
    //               required={true}
    //               name="email"
    //               value={contact.email}
    //               onChange={newValues}
    //               className="form-control my-4"
    //               type={"email"}
    //               placeholder="Contact email"
    //             />
    //           </div>
    //           <div className="col">
    //             <input
    //               required={true}
    //               name="photo"
    //               value={contact.photo}
    //               onChange={newValues}
    //               className="form-control my-4"
    //               type={"text"}
    //               placeholder="Contact photo Url"
    //             />
    //             <img
    //               src={contact.photo}
    //               className="contact-photo"
    //               alt="contact"
    //             />
    //           </div>
    //           <div className="row">
    //             <input
    //               className="form-control col btn btn-outline-light my-4 mx-1"
    //               type={"submit"}
    //               value={btnValue}
    //             />
    //             <Link
    //               to={"/contacts/page"}
    //               className="col form-control btn btn-light my-4"
    //             >
    //               Cancel
    //             </Link>
    //           </div>
    //         </div>
    //       </form>
    //     </div>
    //   ) : (
    //     ""
    //   )}
    // </React.Fragment>
<React.Fragment>
      <Container className="text-center my-4">
        <h2>{title}</h2>
      </Container>
      {!loading ? (
        <Form
          className="col-md-6 bg-dark py-4 px-5"
          onSubmit={submitForm}
          style={{ display: "block", margin: "0 auto" }}
        >
          <Form.Group className="mb-3">
            <Form.Control
              required={true}
              name="name"
              value={contact.name}
              onChange={newValues}
              type={"text"}
              placeholder="Contact name"
              className="my-4"
            />
            <Form.Control
              required={true}
              name="mobile"
              value={contact.mobile}
              onChange={newValues}
              type={"tel"}
              placeholder="Contact mobile"
              className="my-4"
            />
            <Form.Control
              required={true}
              name="email"
              value={contact.email}
              onChange={newValues}
              type={"email"}
              placeholder="Contact email"
              className="my-4"
            />
          </Form.Group>
          <Row>
            <Form.Control
              className="col btn btn-outline-light my-4 mx-1"
              type={"submit"}
              value={btnValue}
            />
            <LinkContainer to={"/contacts/page"} className="col my-4">
              <Button variant="light">Cancel</Button>
            </LinkContainer>
          </Row>
        </Form>
      ) : (
        ""
      )}
    </React.Fragment>

  );
}
export default ContactForm;
